import React, {useState, useEffect} from 'react'
import {Switch, Link} from 'react-router-dom'


const Product = ({id, name, source, product_info, price, color, count}) => {
    const [initCount, setCountIncrement]= useState(1);
      useEffect(()=>{
        setCountIncrement(initCount+1);
      });
      const [initCountDec, setCountDecrement]= useState(1);
      useEffect(()=>{
        setCountDecrement(initCountDec-1);
      });
    return(
        <div className="project-page">
            <h3>{id}</h3>
            <h3>{name}</h3>
            <img src={source}></img>
            <h3>{product_info}</h3>
            <h3>{price}</h3>
            <h3>{color}</h3>
            <h3>{count}</h3>
            <button onClick={setCountIncrement}>+</button>
            <button onClick={setCountDecrement}>-</button>
        </div>
    );
}
export default Product
