import React, {useState, useEffect} from 'react'
import {Switch, Link} from 'react-router-dom'


const Product = ({id, name, source, product_info, price, color, count}) => {
    const [currentcount, setCountIncrement]= useState(0);

    const handleClick1=()=>{
            setCountIncrement(currentcount+1);
      };
    const [currentcountDC, setCountDecrement]= useState(0);
    const handleClick2=()=>{
                console.log(setCountDecrement(currentcountDC-1));
      };
    return(
        <div className="project-page">
            <h3>{id}</h3>
            <h3>{name}</h3>
            <img src={source}></img>
            <h3>{product_info}</h3>
            <h3>{price}</h3>
            <h3>{color}</h3>
            <h3>{count}</h3>
            <div>
                <button onClick={handleClick1}>+</button>
                <div>{currentcount}</div>
            </div>
            <div>
                <button onClick={handleClick2}>-</button>
                <div>{currentcountDC}</div>
            </div>
        </div>
    );
}
export default Product
